$users = get-aduser -Filter * -Server zeus2 -SearchBase "OU=AUHSD Staff,DC=acalanes,DC=k12,DC=ca,DC=us" -Properties * | Where{$_.mail -like "*@auhsdschools.org" -And $_.department -NotLike "Service Account"} | ?{$_.enabled -eq $True} | select name, mail, description, telephonenumber, ipphone, office, department, company
$users += get-aduser -Filter * -Server paris -SearchBase "OU=Acad Staff,DC=staff,DC=acalanes,DC=k12,DC=ca,DC=us" -Properties * | Where{$_.mail -like "*@auhsdschools.org" -And $_.department -NotLike "Service Account"} | ?{$_.enabled -eq $True} | select name, mail, description, telephonenumber,ipphone, office, department, company
$users | export-csv -Path e:\PythonTemp\AllAUHSDStaff.csv -NoTypeInformation
& E:\GAMADV-XTD3\gam.exe csv e:\PythonTemp\AllAUHSDStaff.csv gam update user ~mail phone type work value ~telephonenumber primary organization type work title ~description department ~office primary Campus.Campus ~office Campus.Department ~department
Remove-Item -Path e:\PythonTemp\AllAUHSDStaff.csv
